import css from './Contact.module.css';

export const Contact = ({ contact, deleteBtn }) => {
  return (
    <>
      {contact.map(({ name, number, id }) => {
        return (
          <li key={id} className={css.co}>
            <p>
              {name}: {number}
            </p>
            <button type="button" onClick={() => deleteBtn(id)}>
              delete
            </button>
          </li>
        );
      })}
    </>
  );
};
